
-- summary
-- SELECT pl_month_tb.closing_balance, pl_account_type.name_ln1, pl_account_type.id, pl_account.ref_account_number, it_institute.name_ln2, gl_branch.name_ln1, it_user_master.name, pl_account_type.pl_account_category_id, pl_month_tb.branch_month_id, it_generated_reports.filter_2_value, it_generated_reports.id, pl_month_tb.pl_account_id, pl_account.id, pl_account.ci_customer_id, ci_customer.id, pl_account.pl_account_type_id, pl_account.branch_id, ci_customer.branch_id, gl_branch.id, gl_branch.it_institute_id, it_institute.id, it_branch_month.id, it_generated_reports.branch_id, it_generated_reports.user_id, it_user_master.id FROM pl_month_tb INNER JOIN pl_account ON pl_month_tb.pl_account_id = pl_account.id INNER JOIN ci_customer ON pl_account.ci_customer_id = ci_customer.id INNER JOIN pl_account_type ON pl_account.pl_account_type_id = pl_account_type.id INNER JOIN gl_branch ON pl_account.branch_id = gl_branch.id AND ci_customer.branch_id = gl_branch.id INNER JOIN it_institute ON it_institute.id = gl_branch.it_institute_id INNER JOIN it_branch_month ON it_branch_month.id = pl_month_tb.branch_month_id INNER JOIN it_generated_reports ON it_generated_reports.branch_id = gl_branch.id INNER JOIN it_user_master ON it_user_master.id = it_generated_reports.user_id WHERE pl_month_tb.closing_balance <> 0 AND pl_account_type.pl_account_category_id = 1 AND pl_month_tb.branch_month_id = it_generated_reports.filter_2_value AND it_generated_reports.id = 130 ORDER BY pl_account_type.id ASC, pl_account.ref_account_number ASC;


-- detail
SELECT ci_customer.customer_number, ci_customer.full_name_ln2, pl_account.ref_account_number, pl_account.open_date, pl_month_tb.closing_balance, pl_account_type.name_ln1, it_institute.name_ln2, gl_branch.name_ln1, it_user_master.name, it_generated_reports.id, it_generated_reports.filter_1_value, it_generated_reports.filter_2_value, it_generated_reports.branch_id, pl_account_type.pl_account_category_id, pl_account_type.id, pl_account.ci_customer_id FROM pl_month_tb INNER JOIN pl_account ON pl_month_tb.pl_account_id = pl_account.id INNER JOIN ci_customer ON pl_account.ci_customer_id = ci_customer.id INNER JOIN pl_account_type ON pl_account.pl_account_type_id = pl_account_type.id INNER JOIN gl_branch ON pl_account.branch_id = gl_branch.id AND ci_customer.branch_id = gl_branch.id INNER JOIN it_institute ON it_institute.id = gl_branch.it_institute_id, it_generated_reports INNER JOIN it_user_master ON it_user_master.id = it_generated_reports.user_id WHERE pl_month_tb.closing_balance <> 0 AND it_generated_reports.id = 130 AND pl_account_type.pl_account_category_id = 1 AND pl_month_tb.branch_month_id = it_generated_reports.filter_2_value AND pl_account.gl_account_id = it_generated_reports.filter_1_value AND pl_account.branch_id = it_generated_reports.branch_id ORDER BY pl_account_type.id ASC, pl_account.ci_customer_id ASC, pl_account.ref_account_number ASC;